const range1={1:"یک",2:"دو",3:"سه",4:"چهار",5:"پنج",6:"شش",7:"هفت",8:"هشت",9:"نه",10:"ده",11:"یازده",12:"دوازده",13:"سیزده",14:"چهارده",15:"پانزده",16:"شانزده",17:"هفده",18:"هجده",19:"نوزده"},range10={1:"ده",2:"بیست",3:"سی",4:"چهل",5:"پنجاه",6:"شصت",7:"هفتاد",8:"هشتاد",9:"نود"},range100={1:"صد",2:"دویست",3:"سیصد",4:"چهارصد",5:"پانصد",6:"ششصد",7:"هفتصد",8:"هشتصد",9:"نهصد"},numbersSuffix={1:"هزار",2:"میلیون",3:"میلیارد",4:"تریلیون",5:"بیلیارد"};function wordsWithSuffix(e,n){let r,t=e.map((n,r)=>{if(1==e[1])switch(r){case 0:return range1[parseInt(n)+10];case 2:return range100[parseInt(n)]}else switch(r){case 0:return range1[parseInt(n)];case 1:return range10[parseInt(n)];case 2:return range100[parseInt(n)]}}).filter(e=>e);return r=0!=n&&t.length>0?" "+numbersSuffix[n]+" و ":"",t.reverse().join(" و ")+r}function classifyNumbers(e){let n=(e.length-e.length%3)/3;n+=e.length%3==0?0:1;const r=[];for(let t=0;t<n;t++)r.push(e.slice(3*t,3*t+3));return r}function combineNumbers(e){const n=classifyNumbers(e),r=[];for(let e=0;e<n.length;e++)r.push(wordsWithSuffix(n[e],e));return r.reverse()}function render(e){let n=combineNumbers(e),r="";n.forEach(e=>{r+=e});let t="و"==r[r.length-2]?r.slice(0,r.length-2):r;return t=""==t?"صفر":t,"-"==e[e.length-1]&&(t="منفی "+t),t}function htmlRender(){let e=document.getElementsByClassName("num-to-word");for(let n=0;n<e.length;n++){let r=render(e[n].innerHTML.toString().split("").reverse());e[n].innerHTML=r}}htmlRender();